import { Component, OnInit, Pipe } from '@angular/core';
import { Libro } from 'src/app/models/libro';

@Component({
  selector: 'app-libros',
  templateUrl: './libros.component.html',
  styleUrls: ['./libros.component.css']
})
export class LibrosComponent implements OnInit {
  public miLibro :Libro[];
  public nuevolibro:Libro

  constructor()
   {

    this.miLibro= [new Libro(1,1,"necronomicon","Piel humana","Desconocido",20,"data:image/jpeg;base64,"),
                  new Libro(2,2,"Almanaque deportivo","Grapa","Marty Mcfly",20,"data:image/jpeg;base64,"),
                  new Libro(3,3,"La historia interminable","Tapa dura","Michael Ende",50,"data:image/jpeg;base64,")]
   }

  ngOnInit(): void {
  }
  public enviar (nuevotitulo:HTMLInputElement,nuevotipo:HTMLInputElement,nuevoautor:HTMLInputElement,nuevoprecio:HTMLInputElement,nuevafoto:HTMLInputElement)
  { this.nuevolibro = new Libro(3,3,nuevotitulo.value,nuevotipo.value,nuevoautor.value,Number(nuevoprecio.value),nuevafoto.value)
    
  
 
 this.miLibro.push(this.nuevolibro);
  }

}

